var glob = require("glob");
var file = require("wizzi-core").file;
var artifacts = require("wizzi-lab-artifacts");

glob("in/*.html.ittf", {}, function (er, files) {
    files.forEach(function (item) {
        console.log('in', item);
        if (item.indexOf('/t/') < 0) {
            gen(item);
        }
    });
})

function gen(filepath) {
    artifacts.htmlDocument(filepath, {}, function (err, content) {
        if (err) { console.log(err); return; }
        var o = 'out/' + filepath.substr(3, filepath.length - 8);
        file.write(o, content);
    });
}



