var statement = require('./statement');
var method = module.exports = {};
var myname = 'js.module.method';

method.gen = function (model, ctx) {
    ctx.a('m_name', model.WmtName);
    ctx.a('m_args', model.jsargs() || '');
    // console.log(model.WmtName, model.static);
    if (model.static)
        ctx.w('{c_name}.{m_name} = function({m_args}) {');
    else
        ctx.w('{c_name}.prototype.{m_name} = function({m_args}) {');
    ctx.indent();
    model.statements.forEach(function (item) {
        statement.gen(item, ctx);
    });
    ctx.deindent();
    ctx.w('}');
}